checkout.scss:
// =============================================================================
// Halo Onepage Checkout for Stencil
// =============================================================================

@import url('https://fonts.googleapis.com/css?family=Karla:400,700|Montserrat:400,500,700');
$fontFamily-headings : stencilFontFamily("headings-font"), Arial, Helvetica, sans-serif;
$fontFamily-sans : stencilFontFamily("body-font"), Arial, Helvetica, sans-serif;

h1.TitleHeading {
   font-family: 'Arimo', sans-serif;
   font-size: 20px;
   margin: 30px 0 25px;
   letter-spacing: 0;
   text-transform: uppercase;
   padding: 0 15px 20px 15px;
}
#LayoutColumn2 h1 {
    padding: 0 0 20px 0;
    font-weight: 700;
}
.CheckoutMultiple {
    padding: 0 15px !important;
}
.CheckoutMultiple.Content {
    h1,h2,h3{
        text-transform: uppercase;
        font-size: 20px;
        padding: 0 0 6px;
        color: #2d2d2d;
        font-family: 'Arimo', Arial, Helvetica, sans-serif;
        font-weight: 700;
        @media (max-width: 380px) {
            font-size: 18px;
        }
    }
}
.CheckoutMultiple.Content h2 {
    margin-top: 30px;
}
.CheckoutMultiple .ExpressCheckoutContent .CreateAccount {
    padding-right: 1% !important;
    width: 46%;
    padding-left: 0;
    & ul {
        & li {
            line-height: 22px;
            & label {
                width: 100%;
            }
        }
    }
    .Submit {
      width: 100%;
      input {
        margin-left: 0;
      }
    }
  > p {
    clear: both;
    margin-top: 15px;
    margin-bottom: 0;
    width: 100%;
  }
}
.CheckoutMultiple .ExpressCheckoutContent .AccountLogin {
   width: 46%;
   input[type="text"],
   input.Textbox {
      margin-bottom: 8px;
      font-family: 'Roboto', Arial, Helvetica, sans-serif;
      max-width: 440px;
   }
   #LoginForm {
      .HorizontalFormContainer {
        dt {
          &:nth-last-child(4) {
            display: block;
          }
        }
      }
   }
}
@media (max-width: 767px) {
    .CheckoutMultiple .ExpressCheckoutContent .CreateAccount {
        width: 100%;
        padding-right: 0 !important;
    }
    .CheckoutMultiple .ExpressCheckoutContent .AccountLogin {
        width: 100%;
        padding-left: 0;
        margin-top: 20px;
        input[type="text"],
        input.Textbox {
             margin-bottom: 10px;
          }
    }
}

.CheckoutMultiple #LoginForm {
   & dl {
      dt {
         width: 25%;
         padding-right: 20px;
         vertical-align: middle;
         font-family: 'Arimo', Arial, Helvetica, sans-serif;
         font-size: 12px;
         font-weight: 700;
         color: #000;
         line-height: 22px;
         @media (max-width: 1199px) {
            width: 30%;
         }
         @media (max-width: 991px) {
            width: 100%;
            text-align: left;
            padding-bottom: 0;
         }
         &:last-of-type {
            display: none;
         }
      }
      dd {
         width: 100%;
         display: inline-block;
         padding-left: 0 !important;
         &:last-of-type {
            width: auto;
            display: inline-block;
            a {
               font-family: 'Roboto', Arial, Helvetica, sans-serif;
               font-size: 12px;
               font-weight: 400;
               color: #228dd6;
                border-bottom: 1px dotted #228dd6;
                line-height: 15px;
                margin-top: 6px;
            }
         }
         @media (max-width: 1199px) {
            width: 60%;
         }
         @media (max-width: 991px) {
            width: 100%;
            & input {
                margin-bottom: 10px;
            }
            & input.btn {
                margin-bottom: 0px !important;
            }
         }
      }
   }
}

.CheckoutMultiple .ExpressCheckoutContent {
    padding: 30px 0px;
    & h4 {
        font-size: 14px;
        font-family: 'Arimo', sans-serif;
        font-weight: 700;
        text-transform: capitalize;
        color: #000;
    }
    div.FloatLeft {
        width: 100%;
    }
    .AddShippingAddress,
    .AddBillingAddress {
        .HorizontalFormContainer {
            max-width: 100%;
            @media (min-width: 600px) {
                dd {
                    &:nth-of-type(2n+1) {
                        clear: both;
                    }
                    &:nth-of-type(2n) {
                        margin-top: 0px;
                    }
                    margin-bottom: 10px;
                    &:nth-last-child(-n+2) {
                      margin-bottom: -4px;
                      line-height: 28px;
                      height: auto;
                    }
                }
                dt {
                    &:nth-of-type(2n) {
                        margin-top: -36px;
                    }
                }
                dt,
                dd {
                    padding: 0 !important;
                    width: 49%;
                    text-align: left;
                    float: left;
                    &:nth-of-type(2n+1) {
                       float: left;
                       margin-right: 23px;
                    }
                    &:nth-of-type(2n) {
                       float: left;
                    }
                }
            }
        }
    }
}
@media screen and (max-width: 1100px) {
    .CheckoutMultiple .HorizontalFormContainer dt {
        clear: none;
    }
}
@media screen and (max-width: 1024px) {
    .CheckoutMultiple .AddBillingAddress dd,
    .CheckoutMultiple .AddShippingAddress dd {
        margin-left: 0;
        float: none;
    }
}
.CheckoutMultiple .HorizontalFormContainer p.Submit {
    padding-top: 2px;
}
.CheckoutMultiple .CreateAccount input[type="submit"],
.CheckoutMultiple .AccountLogin input[type="submit"] {
    max-width: 185px;
    min-width: 120px;
    @media (max-width: 480px) {
        max-width: 100%;
    }
}
.CheckoutMultiple .btn {
  min-width: 120px;
}
.CheckoutMultiple .FormContainer {
    margin: 0;
    dt {
      text-align: left;
      width: 170px;
      padding-right: 15px;
    }
    dd {
      width: calc(100% - 170px);
      margin-left: 0;
    }
}
.CheckoutMultiple .ExpressCheckoutBlock p {
    font-size: 12px;
}
.CheckoutMultiple .Moveable {
  .multiStep {
      position: relative;
      overflow: hidden;
      margin-bottom: 15px;
      margin-top: 25px;
      border: 0;
      &:after {
          position: absolute;
          bottom: -1px;
          width: 100%;
          z-index: 2;
          height: 5px;
          left: 0;
          content: '';
          background: #000;
          @media (max-width: 1200px) {
              position: static;
          }
      }
      & li {
          position: relative;
          z-index: 1;
          display: table;
          float: left;
          height: 53px;
          margin-left: -1px;
          margin-right: 0;
          border: 1px solid #d0d0d0;
          @media (max-width: 1200px) {
              width: 100%;
              margin-left: 0;
              margin-bottom: -1px;
          }
          &:first-child {
              margin-left: 0;
              border-top-left-radius: 4px;
          }
          &:last-child {
              border-top-right-radius: 4px;
              margin-bottom: 0;
          }
          & h4{
              font-family: arial;
              font-size: 13px;
              letter-spacing: 0;
              font-weight: 700;
              display: block;
              display: table-cell;
              padding: 0 19px;
              line-height: 51px;
              -webkit-transition: background .3s ease 0s;
              -moz-transition: background .3s ease 0s;
              -ms-transition: background .3s ease 0s;
              -o-transition: background .3s ease 0s;
              transition: background .3s ease 0s;
              vertical-align: middle;
              text-transform: uppercase;
              color: #000;
              background: #fafafa;
              font-family: "Arimo", Arial, Helvetica, sans-serif;
              text-decoration: none !important;
              @media (max-width: 480px) {
                  font-size: 12px;
                  padding: 0 15px;
                  letter-spacing: 0;
              }
              &:focus {
                  outline: none;
              }
          }
          &.ActivePage {
              border: 1px solid #000;
              border-bottom: 0;
              background: #fff;
              position: relative;
              z-index: 2;
              @media (max-width: 1200px) {
                   border: 1px solid #000;
              }
          }
          &.ActivePage h4 {
              border-bottom-color: #fff;
              background: #000;
              color: #fff;
              @media (max-width: 1200px) {
                  background: #000;
                  border-bottom-color: #000;
              }
          }
          &:hover{
              border: 1px solid #000;
              border-bottom: 0;
              background: #fff;
              position: relative;
              z-index: 2;
              @media (max-width: 1200px) {
                      border: 1px solid #000;
              }
              h4 {
                  border-bottom-color: #fff;
                  background: #000;
                  color: #fff;
                  @media (max-width: 1200px) {
                      background: #000;
                      border-bottom-color: #000;
                  }
                  & a {
                    color: #fff;
                  }
              }
          }
      }
  }
  &.ExpressCheckoutBlock {
        border: 0 !important;
        box-shadow: none;
    }
  .ShippingProviderList{
    margin-top: 30px;
    margin-bottom: 25px;
    border: none;
    li {
      border-bottom: none;
      div.radio {
        margin-top: -6px;
      }
      label {
        display: inline-block !important;
        padding-left: 5px;
        min-height: auto;
      }
    }
  }
  .ML20 {
    margin-left: 0;
  }
  & .ShippingAddressList {
    & .btn {
        margin-top: 20px;
    }
  }
}
.CheckoutMultiple input[type=submit] {
    max-width: 380px;
    background: #EA3A02;
    color: #fff;
    &:hover {
      background: #CE3517;
      color: #fff;
    }
}
.CheckoutMultiple #ChooseShippingProvider {
    input[type=submit] {
        background: #EA3A02;
        color: #fff;
        border: none;
        &:hover {
            background: #CE3517;
            color: #fff;
        }
    }
}
.CheckoutMultiple #TabContentAddressMultiple {
    & .General {
        & thead {
            & tr {
                & th {
                    padding: 10px 20px;
                    background: #f5f5f5;
                    @media (max-width: 600px) {
                        display: none;
                    }
                }
            }
        }
        & tbody {
            & tr {
                & td {
                    @media (max-width: 600px) {
                        display: block;
                        margin-top: 0;
                        margin-bottom: 0;
                        padding-left: 0;
                        padding-right: 0;

                        &.Even {
                            border-bottom: none;
                            padding-bottom: 0;
                        }
                    }
                }
            }
        }
    }
}
.CheckoutMultiple #multiAddressForm div.selector {
  @media (max-width: 767px) {
    width: 350px;
  }
  @media (max-width: 600px) {
    width: 290px;
  }
  @media (max-width: 380px) {
    width: 275px;
  }
}
.CheckoutMultiple table.CartContents thead tr th {
    padding: 10px 20px;
}
.CheckoutMultiple .CartContents tfoot .CheckoutRedeemCoupon td .btn {
    background: #EA3A02;
    color: #fff;
    border: none;
    width: auto;
    &:hover {
        color: #fff;
        background: #CE3517;
    }
}

@media (max-width: 767px) {
  .CheckoutMultiple .CartContents.Stylize.General tfoot tr td,
  .CheckoutMultiple .CartContents.Stylize.General tfoot {
    width: 100%;
    display: inline-block;
    box-sizing: border-box;
  }
  .CheckoutMultiple table.CartContents tbody tr td {
    border-bottom: 0;
    width: 24.5%;
  }
  .CheckoutMultiple .CartContents tfoot .SubTotal td {
    border-top: 1px solid #ccc;
  }
  .CheckoutMultiple .CartContents.Stylize.General tfoot tr td.Even {
    padding-right: 20px;
    width: 80%;
    float: left;
  }
  .CheckoutMultiple .CartContents.Stylize.General tfoot tr td.Odd {
    padding-left: 20px;
    width: 20%;
    float: right;
  }
  .CheckoutMultiple table.CartContents tbody tr {
    border-bottom: 1px solid #ccc;
  }
  .CheckoutMultiple .CartContents tfoot .CheckoutRedeemCoupon {
    text-align: left;
  }
  .CheckoutMultiple .CartContents tfoot .CheckoutRedeemCoupon td.Even {
    width: 100% !important;
    padding-right: 0 !important;
  }
  .CheckoutMultiple .CartContents tfoot .CheckoutRedeemCoupon td label {
    width: 100%;
    line-height: 26px;
  }
  .CheckoutMultiple .CartContents tfoot .CheckoutRedeemCoupon td input {
    float: left;
    margin-bottom: 15px;
  }
  .CheckoutMultiple .CartContents tfoot .CheckoutRedeemCoupon #couponcode {
    margin-left: 0;
  }
  .CheckoutMultiple table.CartContents tfoot tr {
    display: block;
    border-top: 1px solid #eee;
    text-align: right;
  }
  .CheckoutMultiple table.CartContents tfoot tr.SubTotal.First {
    border-top: none;
  }
  .CheckoutMultiple .CartContents tfoot .CheckoutRedeemCoupon {
    display: inline-block;
    width: 100%;
    padding: 0;
  }
  .CheckoutMultiple .CartContents tfoot .CheckoutRedeemCoupon td {
    padding-left: 0;
    display: inline-block;
  }
}

@media (max-width: 600px) {
  .CheckoutMultiple .CartContents tfoot .SubTotal {
    border-top: none;
  }
  .CheckoutMultiple .CartContents tfoot .CheckoutRedeemCoupon td input {
    width: 100%;
    max-width: 100% !important;
  }
  .CheckoutMultiple .CartContents.Stylize.General tfoot tr td.Even {
    width: 70%;
  }
  .CheckoutMultiple .CartContents.Stylize.General tfoot tr td.Odd {
    width: 30%;
  }
}

#ConfirmOrder {
    & h4 {
        font-weight: 700;
    }
}
#SimilarMultiProductsByCustomerViews {
    margin-top: 40px;
    & h4 {
        font-weight: 700;
    }
}
.CheckoutProcessPayment {
    & .HorizontalFormContainer {
        & dt {
            text-align: left;
            width: 170px;
            @media (max-width: 600px) {
                width: 100%;
            }
            &:last-of-type {
                @media (max-width: 600px) {
                    width: 170px;
                }
            }
        }
        & dd {
            width: calc(100% - 170px);
            margin-left: 0;
            @media (max-width: 600px) {
                width: 100%;
            }
            & .selector, input {
                max-width: 440px;
                @media (max-width: 600px) {
                    margin-bottom: 10px;
                }
            }
            & small {
                margin-bottom: 10px;
                display: inline-block;
            }
            & br {
                @media (max-width: 600px) {
                    display: none;
                }
            }
            &:last-of-type {
                @media (max-width: 600px) {
                    width: calc(100% - 170px);
                }
            }
        }
    }
}
/* ---------------- One Page Checkout Add on ---------------------------- */
* {
    box-sizing: border-box;
}

#AjaxLoading {
    top: 0;
}

body {
    letter-spacing: 0.02em;
    font-family: $fontFamily-sans;
    font-size: 14px;
    line-height: 22px;
    color: stencilColor('color-textBase');
}

.inner {
    width: 100% !important;
    max-width: 1170px !important;
    margin-left: auto;
    margin-right: auto;
}

.header {
    border: 0;
    height: 96px;
    display: flex;
    align-items: center;
    justify-content: center;
    background: stencilColor('header-backgroundColor');
    background-postion: center;
    background-repeat: no-repeat;
    background-size: cover;
    #Header {

        & .header-logo {
            padding: 5px 0 0 0;
            & a {
                font-family: stencilFontFamily('logo-font'), Arial, Helvetica, sans-serif;
                font-weight: stencilFontWeight('logo-font');
                font-size: 32px;
                color: #fff;
                text-transform: uppercase;
                letter-spacing: 0.1em;
            }
            & img {
                @media (max-width: 600px) {
                    max-width: 90%;
                }
            }
        }
    }
}

input[type="text"],
input[type="email"],
input[type="password"] {
    border-color: stencilColor('input-border-color');
    border-radius: 3px;
    height: 40px;
    line-height: 40px;
    font-family: $fontFamily-sans;
}

input[type="date"],
input[type="email"],
input[type="number"],
input[type="password"],
input[type="search"],
input[type="tel"],
input[type="text"],
input[type="url"],
textarea,
select {
    @media (max-width: 600px) {
        font-size: 14px !important;
    }
}

div.selector,
select {
    border-color: stencilColor('input-border-color');
    border-radius: 4px;
    height: 42px;
    line-height: 42px;
    font-family: $fontFamily-sans;
    min-height: auto !important;
    & span {
        height: 42px;
        line-height: 42px;
        font-family: $fontFamily-sans !important;
    }
}

div.selector {
    background-image: url("data:image/svg+xml;charset=utf8,%3Csvg xmlns='http://www.w3.org/2000/svg' width='24' height='24' viewBox='0 0 24 24'%3E%3Cpath d='M16.59 8.59L12 13.17 7.41 8.59 6 10l6 6 6-6z' fill='rgba(130, 130, 130, 0.999)' /%3E%3C/svg%3E") !important;
    background-position: calc(100% - 5px) !important;
    background-size: 20px !important;
    @media (max-width: 600px) {
        margin-bottom: 20px;
    }
}

#uniform-sel_billing_address {
    margin-bottom: 15px;
    width: 100%;
    & select {
        width: 100% !important;
    }
}

input[type="button"],
input[type="submit"] {
    font-family: Roboto,Arial,Helvetica,sans-serif;
    text-transform: none;
    font-weight: 700;
    letter-spacing: 0;
    font-size: 12px;
    transition: background 0.3s ease 0s;
    border-radius: 3px;
    min-height: 40px;
    background: stencilColor('button--primary-backgroundColor');
    &:hover {
        background: stencilColor('button--primary-backgroundColorHover');
    }
    &:focus,
    &:active {
        background: stencilColor('button--primary-backgroundColorActive');
    }
}

input[type="submit"] {
    width: 100%;
    max-width: 380px;
}

input[type="submit"].billingButton {
    margin-top: 5px;
}

.ENV_onepage .btn:hover {
    background: #000;
}

textarea {
    border-color: #dfdfdf;
}

.AddBillingAddress {
    & p.Submit {
        width: auto;
        margin-top: 0;
        padding-top: 10px
    }
}

#uniform-join_mailing_list {
    &>span {
        position: relative;
        top: -2px;
    }
}

.SelectBillingAddress,
.SelectShippingAddress {
    margin-bottom: 44px;
}

.ENV_opco_none {
    opacity: 0;
    visibility: hidden;
    height: 0;
    padding: 0;
    &.view {
        opacity: 1;
        visibility: visible;
        height: auto;
    }
}


.ENV_onepage.ENV_opco_div {
    display: flex;
    flex-wrap: wrap;
    margin-left: -15px;
    margin-right: -15px;
    @media (max-width: 1199px) {
        margin-left: 0;
        margin-right: 0;
    }
}

dd.checkbox_create_account {
    width: 100%;
}

.AddBillingAddress dd,
.AddShippingAddress dd {
    @media (max-width: 1199px) {
        margin-left: 0;
    }
}

h1.checkout_header {
    width: 100%;
    font-size: 28px;
    font-family: $fontFamily-headings;
    padding-top: 40px;
    padding-bottom: 33px;
    padding-left: 15px;
    padding-right: 15px;
}


form {
    & .FormContainer {
        margin-left: -10px;
        margin-right: -10px;
    }
    dl {
        display: flex;
        flex-wrap: wrap;
        & .div_form {
            width: 50%;
            display: flex;
            flex-wrap: wrap;
            @media (max-width: 480px) {
                width: 100%;
            }
            & dt {
                text-align: left;
                padding-bottom: 10px !important;
                width: 100%;
                padding-right: 10px !important;
                padding-left: 10px !important;
                line-height: 22px;
                font-size: 14px;
                & label {
                    display: flex;
                    flex-direction: row-reverse;
                    justify-content: flex-end;
                    align-items: center;
                    &>span.Required {
                        position: relative;
                        top: 3px;
                        color: #f00;
                    }
                }
            }
            & dd {
                margin: 0;
                width: 100%;
                padding-right: 10px !important;
                padding-left: 10px !important;
                line-height: 22px;
            }
        }

        &>dd:not(.div_form) {
            padding-left: 10px !important;
            padding-bottom: 0 !important;
        }
    }
    label {
        font-size: 14px;
    }
}


/*Popup Login*/

.box_bg {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgba(69, 69, 69, .8);
    z-index: 999;
    transition: all 0.3s ease 0s;
    display: none;
}

.env_login.ENV_opco_none {
    opacity: 0;
    visibility: hidden;
    position: fixed;
    background: #fff;
    transition: all 0.3s ease 0s;
    top: 0;
    width: 100%;
    max-width: 470px;
    height: 100%;
    max-height: 310px;
    top: 0%;
    left: 50%;
    transform: translate(-50%, -50%);
    padding: 28px 50px 23px;
    @media (max-width: 480px) {
        padding: 28px 30px 23px;
        max-height: 90%;
    }
    &.view {
        opacity: 1;
        visibility: visible;
        top: 50%;
        z-index: 1000;
    }
    & .ExpressCheckoutTitle {
        font-family: $fontFamily-headings;
        font-weight: 400;
        font-size: 22px;
        margin: 0;
    }
    & .ExpressCheckoutContent {
        position: relative;
        & a.modal-close {
            position: absolute;
            top: -28px;
            right: -10px;
            & span {
                font-size: 26px;
                display: flex;
                width: 34px;
                height: 34px;
                justify-content: center;
                align-items: center;
            }
        }
        & h4 {
            display: none;
        }
        & #LoginIntro {
            display: none;
        }
        & form#LoginForm {
            padding-top: 33px;
            margin-left: -10px;
            margin-right: -10px;
            & .FormContainer {
                margin: 0;
            }
            dl {
                display: flex;
                flex-wrap: wrap;
                & .div_form {
                    width: 50%;
                    display: flex;
                    flex-wrap: wrap;
                    @media (max-width: 480px) {
                        width: 100%;
                    }
                    & dt {
                        text-align: left;
                        line-height: 20px;
                        padding-bottom: 10px !important;
                        width: 100%;
                        padding-right: 10px !important;
                        padding-left: 10px !important;
                    }
                    & dd {
                        margin: 0;
                        width: 100%;
                        padding-right: 10px;
                        padding-left: 10px;
                        line-height: 22px;
                    }
                }
                & .div_form_2 {
                    width: 100%;
                    & dt {
                        display: none;
                    }
                    & dd {
                        & input#LoginButton {
                            width: 100%;
                        }
                        padding-bottom: 25px;
                    }
                }
