/* Custom Styles */

body.is-sticky {
  .header-top-middle {
    opacity: 0;
  }
}

.header-top-middle {
  padding: 0;
  &.mobile {
    background-color: #28282d;
    display: block;
    width: 100%;
    color: #fff;
    text-align: center;
    .promos {
      padding: 0 10px;
      justify-content: center;
      img {
        min-width: 60px;
      }
    }
  }

  .promos {
    height: 64px;
    display: flex;
    align-items: center;
    text-align: center;
    justify-content: center;
    .promo-img {
      max-width: 50px;
      margin-right: 10px;
    }
  }
}

.sticky_logo {
  display: none;
}
@media (min-width: 1200px) {
  .is-sticky .sticky_logo {
    display: block;
    position: absolute;
    top: 7px;
    left: 20px;
    z-index: 9;
    width: 100px;
    height: 50px;
    background: url(https://cdn11.bigcommerce.com/s-8mji1/product_images/uploaded_images/icon-sprites.png?t=1571867528&_ga=2.234388446.1139666671.1571855623-1347947521.1571855623) -51px 59px;
  }
  .is-sticky .header-top{
      position: absolute;
      background: #28282d;
      color: white;
      width: 100%;
      z-index: 9;
  }
  .is-sticky .header .container{
    width: 100% !important;
  }
  .is-sticky .header-top .header-top-left {
      z-index: 9999;
      width: 27%;
      margin-left: 13%;
      margin-top: 7px;
  }
  .is-sticky .header-top .header-top-left ul.navPages-list {
    margin-top: 5px;
  }
  .is-sticky .header-logo {
      width: 10%;
      text-align: center;
      margin-top: 10px;
      z-index: 9999;
      position: relative;
      display: none;
  }
  .is-sticky #quickSearch {
      width: 30%;
      position: absolute;
      left: 40%;
      z-index: 9999;
      top: 0px;
      padding: 12px 0 12px 0;
  }
  .is-sticky .header-top .header-top-right {
      position: absolute;
      top: 21px;
      padding: 0;
      left: 70%;
      width: 20%;
      z-index: 9999;
  }
  .is-sticky .header-middle-right {
      width: 6%;
      position: absolute;
      top: 0;
      left: 92%;
      z-index: 9999;
      margin-top: 12px;
  }
  .is-sticky #quick-menu{
    display: none;
  }
  .is-sticky #quickSearch .quickSearchResultsWrap {
    width: 100%;
    left: 0;
  }
}
@media (max-width: 1550px) {
  .is-sticky .header-middle-right {
    width: 8%;
    left: 90%;
  }
  .is-sticky .header-top .header-top-right {
    width: 18%;
  }
}

@media (max-width: 1199px) {
  .is-sticky .header-top .header-top-left {
    width: 22%;
    margin-left: 11%;
  }
}
