/* Custom Pages */
.custom-page {
  & h5 {
    font-weight: 900;
  }
  & p {
    font-size: 16px;
  }
  & .btn-module-heading {
    height: 50px;
    line-height: 50px;
    box-shadow: 0px 3px 1px darken( stencilColor('module-heading-button-background'), 11% );
    font-size: 20px;
    font-weight: 400;
    padding: 0 20px;
    @media (max-width: 480px) {
      font-size: 18px;
    }
    @media (max-width: 380px) {
      font-size: 15px;
      padding: 0 15px;
    }
    &:hover {
      box-shadow: 0px 3px 0px #222222;
    }
    & svg {
      width: 18px;
      height: 16px;
      margin-top: 1px;
      letter-spacing: 0.055em;
    }
  }
  .container {
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-align: center;
    -webkit-align-items: center;
    align-items: center;
    padding: 0px;
    width: 100%;

    @media (max-width: 991px) {
      justify-content: center;
      -ms-justify-content: center;
      -webkit-justify-content: center;
    }
    @media (max-width: 767px){
      justify-content: center;
      padding: 0 20px;
    }
    & p {
      color: stencilColor('color-white');
    }
    & h3, h5 {
      color: stencilColor('color-white');
      text-transform: uppercase;
      font-weight: 900;
    }
  }
  & .banner-text {
    position: relative;
    > img {
      height: 600px;
      object-fit: cover;
    }
    & .container {
      & .col-xs-6 {
        & h5 {
          border-bottom: 1px solid stencilColor('main-color');
          padding-bottom: 7px;
          max-width: 372px;
        }
        & h3 {
          margin-top: 14px;
          margin-bottom: 28px;
        }
        & p {
          font-size: 16px;
          line-height: 30px;
          margin-bottom: 42px;
        }
      }
    }
  }
}
.full-width-one-banner {
  width: 100%;
  float: left;
  & .banner-content {
    width: 50%;
    @media (max-width: 991px){
      width: 80%;
    }
    @media (max-width: 767px){
      width: 100%;
    }
  }
  & .banner-text {
      margin-bottom: 2px;
      & > img {
          width: 100%;
          @media (max-width: 767px) {
            height: 600px;
            object-fit: cover;
          }
          @media (max-width: 480px) {
            height: 570px;
          }
        }
      & .banner-content {
        & h1 {
          color: stencilColor('color-white');
          font-size: 68px;
          font-weight: 800;
          line-height: 78px;
          display: inline-block;
          @media (max-width: 1199px){
            font-size: 58px;
            line-height: 58px;
          }
          @media (max-width: 991px){
            margin-top: 0;
            width: 70%;
          }
          @media (max-width: 767px){
            font-size: 47px;
            line-height: 54px;
            width: 90%;
            margin-top: 30px;
          }
          @media (max-width: 580px){
             margin-top: 20px;
          }
          @media (max-width: 480px){
            width: 100%;
            text-align: left;
            margin-bottom: 40px;
          }
          @media (max-width: 380px){
            font-size: 38px;
            line-height: 44px;
          }
          & span {
            color: stencilColor('main-color');
          }
        }
        & h3, h5 {
          margin-bottom: 30px;
        }
        & h5 {
          padding-bottom: 10px;
          max-width: 370px;
          @media (max-width: 767px) {
            max-width: 100%;
          }
        }
        & .banner-logo {
          width: 100%;
          display: inline-block;
          text-align: right;
        }
         & p {
            font-size: 16px;
            line-height: 25px;
            margin-bottom: 30px;
            @media (max-width: 480px){
              line-height: 26px;
              margin-bottom: 40px;
              text-align: left;
              font-size: 14px;
            }
        }
        & ul.bottom-cate {
          position: absolute;
          bottom: 18px;
          left: 20px;
          right: 20px;
          border-top: 1px solid stencilColor('main-color');
          width: calc(100% - 40px);
          max-width: 600px;
          margin: 0;
          display: -ms-flexbox;
          display: -webkit-flex;
          display: flex;
          -ms-flex-align: center;
          -webkit-align-items: center;
          align-items: center;
          -ms-flex-pack: center;
          -webkit-justify-content: flex-start;
          justify-content: flex-start;
          flex-wrap: wrap;
          @media (max-width: 991px){
            padding-top: 10px;
            max-width: 100%;
            width: calc(80% - 40px);
            left: auto;
            right: auto;
          }
          @media (max-width: 767px) {
            max-width: 100% !important;
            width: 100%;
            left: 0 !important;
            padding-top: 15px;
            padding-bottom: 10px;
          }
          & li {
            width: 16.66%;
            float: left;
            text-align: center;
            padding: 20px 15px;
            list-style: none;
            line-height: 15px;
            @media (max-width: 1199px){
              width: 33.33%;
              padding-bottom: 10px;
            }
            @media (max-width: 991px){
              padding: 20px 30px;
              width: 16.66%;
            }
            @media (max-width: 767px){
              width: 33.33%;
              padding: 15px 30px;
            }
            @media (max-width: 580px){
              padding: 15px;
            }
            @media (max-width: 480px){
              padding: 10px 20px;
            }
            & a {
              color: stencilColor('color-white');
              text-transform: uppercase;
              font-weight: 900;
              font-size: 11px;
            }
          }
        }
      }
      & .inner-wrap {
          position: relative;
          width: 100%;
          height: 600px;
          & .inner {
            position: absolute;
            top: 0;
            bottom: 0;
            left: 18%;
            right: 18%;
            display: -ms-flexbox;
            display: flex;
            -ms-flex-align: center;
            align-items: flex-end;
            width: 64%;
            @media (max-width: 1199px) {
              width: 100%;
              left: 0;
              right: 0;
              padding: 0 20px;
            }
            @media (max-width: 991px) {
              width: 100%;
              left: 0;
              right: 0;
              padding: 0;
            }
          }
        }
  }
  & .banner-text-left {
    .banner-content {
      float: left;
      @media (max-width: 767px) {
        padding-right: 0px;
      }
      & ul.bottom-cate {
        @media (max-width: 1199px){
          bottom: 8px;
        }
      }
    }
  }
  & .banner-text-center {
    & .container {
      text-align: center;
      justify-content: center;
      -webkit-justify-content: center;
    }
  }
  & .banner-text-right {
    & > img {
      object-fit: cover;
      @media (max-width: 1700px) {
        height: 600px;
      }
      @media (max-width: 767px) {
        width: 100%;
        max-width: 100%;
      }

    }
    & .container {
      @media (min-width: 992px) {
       justify-content: flex-end;
       -ms-justify-content: flex-end;
       -webkit-justify-content: flex-end;
      }
      .banner-content {
        float: right;
        text-align: right;

        @media (max-width: 991px) {
          width: 80%;
        }
        @media (max-width: 767px) {
          width: 100%;
          text-align: center;
        }

        & h5 {
          font-size: 16px;
          width: 100%;
          float: right;
          margin-bottom: 32px;
          @media (max-width: 767px) {
            text-align: right;
            margin-bottom: 25px;
          }
        }
        & h3 {
          font-size: 24px;
          line-height: 24px;
          clear: both;
          @media (max-width: 767px) {
            line-height: 28px;
            font-size: 22px;
            text-align: right;
          }
        }
        & p {
          @media (max-width: 767px) {
            margin-bottom: 45px;
            text-align: center;
          }
        }
        & ul.bottom-cate {
          left: auto;
          text-align: right;
          @media (max-width: 767px) {
            left: 0;
          }
        }
      }
    }
  }
  &.two-images {
    & .banner-text {
      width: 100%;
      float: left;
    }
    & .container {
      & .banner-content {
        padding-bottom: 100px;
        @media (max-width: 1199px){
          padding-bottom: 120px;
        }
        @media (max-width: 991px){
          padding-bottom: 120px;
        }
      }
    }
    & .banner-text-left {
      & .images-wrap {
        & .col-md-6 {
          padding: 0;
          & img {
            width: 100%;
            height: 674px;
            object-fit: cover;
            @media (max-width: 480px) {
              height: 634px;
            }
          }
          &.right-col {
            @media (max-width: 767px) {
              display: none;
            }
          }
        }
      }
      & .container {
        padding: 0 20px;
        @media (min-width: 1420px) {
          width: 1220px;
        }
        @media (max-width: 991px) {
            height: 50%;
            padding: 0;
        }
        @media (max-width: 767px) {
            height: 100%;
        }
        @media (max-width: 480px) {
          padding: 0;
        }
        & .banner-content {
          padding-right: 30px;
          @media (max-width: 991px) {
            width: 80%;
            padding-right: 0;
          }
          @media (max-width: 767px) {
            width: 100%;
            padding-left: 20px;
            padding-right: 20px;
            text-align: center;
          }
          & ul.bottom-cate {
            max-width: 540px;
            width: calc(50% - 40px);
            @media (max-width: 991px) {
              max-width: 100%;
              width: 80%;
            }
            @media (max-width: 767px) {
              width: 100%;
            }
          }
        }
      }
    }
    & .banner-text-right {
      & img {
        @media (max-width: 480px) {
          height: 500px;
        }
      }
      & .container {
        @media (max-width: 991px) {
          background-color: rgba(0, 0, 0, 0.5);
        }
        & .banner-content {
          padding-bottom: 50px;
          @media (max-width: 1199px) {
            padding-bottom: 0px;
          }
          @media (max-width: 480px) {
            padding-bottom: 0px;
          }
          & ul.bottom-cate {
            -webkit-justify-content: flex-end;
            justify-content: flex-end;
            right: 0;
            left: 0;
            width: 100%;
            @media (max-width: 767px) {
              display: none;
            }
            & li {
              display: inline-block;
              float: none;
              width: auto;
              padding: 15px;
              &:last-child {
                padding-right: 0;
                @media (max-width: 1199px) {
                  padding-right: 15px;
                }
              }
            }
          }
          & .inner-wrap {
            & .inner {
              padding-bottom: 120px;
              @media (max-width: 767px){
                padding-bottom: 0;
                -ms-flex-align: center;
                -webkit-align-items: center;
                align-items: center;

              }
            }
          }
        }
      }
    }
  }
  &.block-7 {
    & .banner-content {
      @media (max-width: 767px){
        padding-bottom: 0px;
      }
      & p {
        @media (max-width: 767px){
          margin-bottom: 25px;
        }
      }
    }
  }
}

.full-width-two-banner {
  width: 100%;
  float: left;
  position: relative;
  margin-bottom: 2px;
  & .banner-text {
    position: relative;
    width: 100%;
    float: left;
    & > .col-xs-6 {
      position: relative;
      padding-left: 0;
      padding-right: 0;
      @media (max-width: 991px) {
        width: 100%;
      }
      & .image-wrap {
        & img {
          width: 100%;
          height: 600px;
          object-fit: cover;
        }
      }
      & .inner-wrap {
        position: absolute;
        top: 0;
        bottom: 0;
        left: 18%;
        right: 18%;
        display: -ms-flexbox;
        display: -webkit-flex;
        display: flex;
        -ms-flex-align: center;
        -webkit-align-items: center;
        align-items: flex-end;
        width: 64%;

        @media (max-width: 1199px) {
          width: 100%;
          left: 0;
          right: 0;
          padding: 0 20px;
        }
        @media (max-width: 991px) {
          width: 80%;
          left: 10%;
          right: 10%;
          padding: 0;
        }
        @media (max-width: 767px) {
          width: 100%;
          padding: 0 20px;
          left: 0;
          right: 0;
        }
        & .inner {
          padding-bottom: 65px;
          & h5, h3 {
            color: stencilColor('color-white');
            font-weight: 900;
          }
          & h5 {
            font-size: 16px;
            border-bottom: 1px solid stencilColor('main-color');
            padding-bottom: 10px;
            max-width: 370px;
            width: 100%;
            display: inline-block;
          }
          & h3 {
            font-size: 24px;
            line-height: 24px;
            margin-top: 8px;
            margin-bottom: 30px;
          }
          & p {
            color: stencilColor('color-white');
            font-size: 16px;
            line-height: 30px;
            margin-bottom: 25px;
          }
        }
      }
      & .images-inner {
        width: 100%;
        margin: 0 auto;
        @media (max-width: 1199px) {
          margin-top: 10px;
          margin-bottom: 20px;
          float: left;
        }
        & h5, h3 {
           text-transform: capitalize;
           font-weight: 900;
           font-size: 16px;
        }
        & h3 {
          @media (max-width: 480px) {
            font-size: 14px;
          }
          @media (max-width: 380px) {
            font-size: 13px;
          }
        }
        & h5 {
          text-align: center;
          line-height: 30px;
          margin-top: 20px;
          margin-bottom: 0;
          position: relative;
          text-transform: none;
          @media (max-width: 767px) {
            padding: 0 20px;
          }

          &:before {
            content: "";
            border-bottom: 1px solid stencilColor('main-color');
            padding-bottom: 6px;
            position: absolute;
            position: absolute;
            bottom: -2px;
            width: 256px;
            left: 50%;
            margin-left: -128px;
          }
        }
        & h3 {
          margin-top: 15px;
        }
        & ul {
          margin-top: 10px;
          margin-left: 0;
          display: -ms-flexbox;
          display: -webkit-flex;
          display: flex;
          -ms-flex-align: center;
          -webkit-align-items: center;
          align-items: center;
          -ms-flex-pack: center;
          -webkit-justify-content: center;
          justify-content: center;
          flex-wrap: wrap;
          @media (max-width: 991px) {
            margin-top: 20px;
          }
          & li {
            text-align: center;
            list-style: none;
            margin-top: 5px;
            margin-bottom: 20px;
            padding: 0 20px;
            @media (max-width: 1199px) {
              margin-bottom: 5px;
            }
            @media (max-width: 991px) {
              margin-bottom: 25px;
            }
            @media (max-width: 480px) {
              padding: 0 15px;
            }
          }
        }
      }
    }
    &.banner-text-left {

    }
    &.banner-text-right {
      & .left-col {
        & .images-inner {
          & ul {
            & li {
              & h3 {
                margin-top: 48px;
              }
            }
            &.four-columns {
              & li {
                @media (min-width: 992px) and (max-width: 1199px) {
                  margin-bottom: 0;
                  margin-top: 10px;
                }
                & h3 {
                  margin-top: 12px;
                }
              }
            }
          }
        }
