/*Halo Popup Login*/
#halo-popup-login {
   max-width: 400px;
   margin: 0;
   @media (max-width: 480px) {
    max-width: 85%;
   }
   & .modal-body {
      padding: 40px;
      @media (max-width: 480px) {
        padding: 40px 20px;
       }
      & h3 {
         margin: 0 0 20px;
         padding: 5px 0;
         text-align: left;
         font-weight: 700;
         text-transform: uppercase;
         position: relative;
         font-size: 20px;
         &::before {
            content: "";
            height: 3px;
            top: -10px;
            left: 0;
            width: 40px;
            background: #00b9e4;
            position: absolute;
         }
      }
      & form {
         & .form-field {
            margin-bottom: 15px;
            & input {
               border-radius: 0;
            }
            & .form-inlineMessage {
               line-height: 1.5em;
               margin: 0;
               font-size: 11px;
            }
         }
         & .form-actions {
            & input[type="submit"] {
               width: 100%;
               border-radius: 0;
               font-size: 11px;
            }
            & a.forgot-password {
               margin: 15px 0 10px;
            }
         }
      }
      & .separated-form {
         & > span {
            display: block;
            position: relative;
            font-weight: 700;
            font-size: 11px;
            font-style: italic;
            text-align: center;
            &::before {
               content: '';
               border-top: 1px solid #ddd;
               width: 40%;
               display: block;
               position: absolute;
               top: 50%;
               left: 0px;
            }
            &::after {
               content: '';
               border-top: 1px solid #ddd;
               width: 40%;
               display: block;
               position: absolute;
               top: 50%;
               right: 0px;
            }
         }
         & a.btn {
            width: 100%;
            margin-top: 20px;
            border-radius: 0;
            font-size: 11px;
         }
      }
   }
}

