landing-page.scss:
.landing-page {
    background: #fff;
    padding-bottom: 0 !important;
    .hero-section {
        background: #f5f5f5;
        padding: 80px 0 25px;
        @media (max-width: 991px) {
                padding: 40px 0 25px;
        }
        .container {
            max-width: 1000px;
        }

        h1 {
            font-size: 45px;
            text-align: center;
            margin-bottom: 25px;
            @media (max-width: 991px) {
                font-size: 35px;
            }
        }
        .sub-heading {
            color: #00b9e4;
            font-size: 25px;
            text-align: center;
            margin-bottom: 40px;
            @media (max-width: 991px) {
                font-size: 21px;
                line-height: 1.4;
                margin-bottom: 30px;
            }
        }
        .hero-image {
            width: 100%;
            text-align: center;
            margin-bottom: 25px;
        }
        .download-guide {
            @media (max-width: 991px) {
                text-align: center;   
            }
            .zogics-or-button {
                text-transform: uppercase;
                height: 47px;
                line-height: 47px;
                padding: 0 2rem;
                text-decoration: none;
            }
            ul {
                padding: 0;
                margin: 5px 0 0;
                float: right;
                @media (max-width: 991px) {
                    float: none;
                    margin: 25px 0 0;
                }
                li {
                    list-style: none;
                    display: inline-block;
                    margin: 0 10px;
                    a {
                        &:hover {
                            opacity: 0.87;
                        }
                    }
                }
                li:last-child {
                    margin-right: 0;
                }
            }
        }
    }

    .content-section {
        background: #fff;
        padding: 60px 0;
        display: inline-block;
        width: 100%;
        font-size: 15px;
        color: #38383e;

        .sidebar-navigation {
            padding: 10px;
            box-shadow: 0 0 10px rgba($color: #999, $alpha: .3);
            border-radius: 5px;
            background: #fff;
            z-index: 9;
            
            @media (max-width: 991px) {
                margin-bottom: 50px;
            }

            .title {
                color: #00b9e4;
                font-weight: 600;
                font-size: 12px;
                line-height: 1.5;
                margin-bottom: 6px;
            }
            ul {
                padding: 0;
                margin: 0;
                li {
                    list-style: none;
                    border-top: 1px solid #bcbcbe;
                    &:first-child {
                        border-top: none;
                    }
                    a {
                        color: #38383e;
                        padding: 6px 0;
                        display: inline-block;
                        width: 100%;
                        font-size: 12px;
                    }
                }
                li.active {
                    a {
                        color: #00b9e4;
                        font-size: 12px;
                    }
                }
            }
        }

        .fixed {
            position: fixed;
            top: 85px;
            width: 265px;
            @media (max-width: 1140px) {
                width: 230px;
            }
            @media (max-width: 991px) {
                position: relative;
                width: 100%;
                top: auto;
            }
        }

        .inner-content {
            h2 {
                margin: 0;
                font-size: 18px;
                font-weight: 500;
                color: #38383e;
                @media (max-width: 991px) {
                    line-height: 1.5;
                }
            }
            p {
                line-height: 1.8;
                margin-top: 40px;
                margin-bottom: 0;
                @media (max-width: 991px) {
                    margin-top: 20px;
                }
            }
            img {
                margin: 60px 0;
                @media (max-width: 991px) {
                    margin: 30px 0;
                }
            }
            ul {
                margin: 20px 0 0 50px;
                padding: 0;
                li {
                    margin-bottom: 25px;
                }
            }
        }
    }

    .full-background-section {
        background: #00b9e4;
        padding: 80px 0;
        color: #fff;
        display: inline-block;
        width: 100%;
        @media (max-width: 991px) {
            padding: 40px 0;       
        }
        .title {
            font-size: 30px;
            font-weight: 600;
            @media (max-width: 991px) {
                line-height: normal;
                font-size: 25px;
            }
        }
        p {
            margin: 40px 0 0;
            font-size: 18px;
            line-height: 1.6;
            @media (max-width: 991px) {
                font-size: 16px;   
            }
        }
        .source-link {
            font-size: 13px;
        }
    }

    .content-section.lobbies {

        .sub-heading {
            font-size: 18px;
            color: #00b9e4;
            font-style: italic;
            margin-bottom: 6px;
            float: left;
            width: 100%;
        }

        h2 {
            font-size: 30px;
            font-weight: 600;
            @media (max-width: 991px) {
                line-height: 1.5;
            }
        }

        p {
            margin-top: 25px;
            a {
                text-decoration: underline;
                &:hover {
                    text-decoration: none;
                }
            }
        }
        img {
            margin: 25px 0 15px;
        }
        .zogics-or-button {
            float: right;
            height: 47px;
            line-height: 47px;
            padding: 0 2rem;
            text-transform: uppercase;
            text-decoration: none;
            @media (max-width: 991px) {
                float: left;
                margin-top: 20px;
            }
        }

        .features {
            background: #00b9e4;
            padding: 30px;
            color: #fff;
            border-radius: 5px;
            margin: 40px 0;
            .title {
                font-weight: 600;
                margin-bottom: 10px;
                font-size: 16px;
            }
            ul {
                margin: 0 0 0 18px;
                padding: 0;
                li {
                    margin: 0 0 5px 0;
                    padding: 0;
                    font-size: 16px;
                    a {
                        font-size: 16px;
                        color: #fff;
                    }
                }
            }
        }

        .features.black {
            background: #38383e;
        }

    }

}
