components/common/footer:
{{#if theme_settings.bigcommerce_banners }}
    {{#if settings.data_tag_enabled}}
        <div class="banners" data-banner-location="bottom">
            {{#each (limit banners.bottom_metadata 1)}}
                <div class="banner container" data-event-type="promotion" data-entity-id="{{this.id}}" data-name="{{this.banner-name}}" data-position="{{this.location}}" data-name="{{this.banner-name}}">
                    <div data-event-type="promotion-click">
                        {{{this.content}}}
                    </div>
                </div>
            {{/each}}
        </div>
        {{else}}
        <div class="banners" data-banner-location="bottom">
            <div class="banner container">
                {{{limit banners.bottom 1}}}
            </div>
        </div>
    {{/if}}
{{/if}}

<div class="newsletter-subscription">
    <div class="container">
        {{#if settings.show_newsletter_box}}
            {{> components/common/subscription-form}}
        {{/if}}
    </div>
</div>

{{#if page_type '!=' 'default' }}

<!-- BEGIN Value Props Block -->
